import thumbsUp from '../assets/img/thumbs-up.svg';
import thumbsDown from "../assets/img/thumbs-down.svg";
import kanyeWest from '../assets/img/kanye.png';

const VotingCards = () => {

    /// to persist data below
    const persistVoting = () => {
        // if thumbsUp or thumbsDown are pressed
